// 1. Get the input data
const inputData = $input.first().json;

// 2. Dig for the text based on your specific error log
// Path: output -> Item 0 -> content -> Item 0 -> text
let rawText = "";

if (inputData.output && inputData.output[0] && inputData.output[0].content && inputData.output[0].content[0]) {
    rawText = inputData.output[0].content[0].text;
} 
// Fallback checks just in case
else {
    rawText = inputData.message?.content || inputData.text || inputData.content;
}

// 3. Safety Check
if (!rawText) {
    return [{ json: { error: "Could not find text", debug: inputData } }];
}

// 4. Clean the text
// Your log shows the text starts with "json\n", so we remove "json" and backticks
const cleanJson = rawText
    .replace(/^json\s*/, "") // Removes the word "json" at the start
    .replace(/```json/g, "") // Removes markdown
    .replace(/```/g, "")
    .trim();

// 5. Parse and Return
try {
    const parsedData = JSON.parse(cleanJson);
    return [{ json: parsedData }];
} catch (e) {
    return [{ json: { error: "Parsing failed", raw_text: rawText } }];
}
